  <div class="slick-image-slide" style="height:<?php echo $sliderheightv; ?>px;">			<div class="slide-wrap" style="height:<?php echo $sliderheightv; ?>px;">				<?php the_post_thumbnail('url'); ?>				<div class="slider-content">				<div class="bg-overlay wp-medium-7 wpcolumns">				<h2 class="slide-title"><?php the_title(); ?></h2>			<?php if($showContent == "true" ) { ?>				<div class="slider-short-content">					<?php the_content(); ?>							</div>			<?php } 					$sliderurl = get_post_meta( get_the_ID(),'wpsisac_slide_link', true );				if($sliderurl != '') { ?>			<div class="readmore"><a href="<?php echo $sliderurl; ?>" class="slider-readmore"><?php esc_html_e( 'Read More', 'wp-slick-slider-and-image-carousel' ); ?></a></div>				<?php } ?>				</div>				</div>			</div>				</div>