<?php function get_wpsisac_carousel_slider( $atts, $content = null ){       extract(shortcode_atts(array(		"limit"     => '',		"category" => '',		"design" => '',		"image_size" => '',				"slidestoshow" => '',		"slidestoscroll" => '',				"dots"     			=> '',		"arrows"     		=> '',		"autoplay"     		=> '',			"autoplay_interval"  => '',		"speed"             => '',		"centermode"        => '',			"variablewidth"    => '',			), $atts));	// required enqueue	wp_enqueue_script( 'wpos-slick-jquery' );	if( $limit ) { 		$posts_per_page = $limit; 	} else {		$posts_per_page = '-1';	}	if( $category ) { 		$cat = $category; 	} else {		$cat = '';	}		if( $design ) { 		$sliderdesign = $design; 	} else {		$sliderdesign = 'design-6';	}			if( $image_size ) { 		$sliderimage_size = $image_size; 	} else {		$sliderimage_size = 'medium';}		if( $dots ) { 		$dotsv = $dots; 	} else {		$dotsv = 'true';	}	if( $arrows ) {		$arrowsv = $arrows; 	} else {		$arrowsv = 'true';	}		if( $autoplay ) { 		$autoplayv = $autoplay;	} else {		$autoplayv = 'true';	}		if( $autoplay_interval ) { 		$autoplayIntervalv = $autoplay_interval; 	} else {		$autoplayIntervalv = '3000';	}		if( $speed ) { 		$speedv = $speed;	} else {		$speedv = '300';	}			if( $slidestoshow ) { 		$slidestoshowv = $slidestoshow;	} else {		$slidestoshowv = '3';	}			if( $slidestoscroll ) { 		$slidestoscrollv = $slidestoscroll;	} else {		$slidestoscrollv = '1';	}		if( $centermode ) { 		$centermodev = $centermode;	} else {		$centermodev = 'false';	}	if( $variablewidth ) { 		$variablewidthv = $variablewidth;	} else {		$variablewidthv = 'false';	}	ob_start();		$unique			= wpsisac_get_unique();	$post_type 		= 'slick_slider';	$orderby 		= 'post_date';	$order 			= 'DESC';		        $args = array (             'post_type'      => $post_type,             'orderby'        => $orderby,             'order'          => $order,            'posts_per_page' => $posts_per_page,                         );	if($cat != ""){            	$args['tax_query'] = array( array( 'taxonomy' => 'wpsisac_slider-category', 'field' => 'id', 'terms' => $cat) );            }              $query = new WP_Query($args);      $post_count = $query->post_count;                      if ( $query->have_posts() ) :			 ?>		<div class="wpsisac-slick-carousal-<?php echo $unique; ?> wpsisac-slick-carousal <?php echo $sliderdesign; ?> <?php if($centermodev == 'true') { echo 'center';} elseif($variablewidthv == 'true') { echo 'variablewidthv'; }  else { echo 'simplecarousal';} ?>">				<?php while ( $query->have_posts() ) : $query->the_post();				switch ($sliderdesign) {				 case "design-6":					include('designs/design-6.php');					break;								 default:		 						include('designs/design-6.php');					}					endwhile; ?>		  </div><!-- #post-## -->				  <?php            endif;              wp_reset_query(); ?><script type="text/javascript">		jQuery(document).ready(function(){		jQuery('.wpsisac-slick-carousal-<?php echo $unique; ?>').slick({			dots: <?php echo $dotsv; ?>,			infinite: true,			arrows: <?php echo $arrowsv; ?>,			speed: <?php echo $speedv; ?>,			autoplay: <?php echo $autoplayv; ?>,			autoplaySpeed: <?php echo $autoplayIntervalv; ?>,			slidesToShow: <?php echo $slidestoshowv; ?>,			slidesToScroll: <?php echo $slidestoscrollv; ?>,			centerMode: <?php echo $centermodev; ?>,			variableWidth :<?php echo $variablewidthv; ?>,			responsive: [    {      breakpoint: 769,      settings: {        slidesToShow: 2,        slidesToScroll: 1,        infinite: true,        dots: false      }    },    {      breakpoint: 641,      settings: {        slidesToShow: 2,        slidesToScroll: 1,		 dots: false      }    },    {      breakpoint: 481,      settings: {        slidesToShow: 1,        slidesToScroll: 1,		 dots: false      }    }  ]		});	});	</script><?php			 		return ob_get_clean();	             	}add_shortcode('slick-carousel-slider','get_wpsisac_carousel_slider');